package main

import (
	"fmt"
	"net/http"
)

func main() {
	// define the header function
	http.HandleFunc("/", func(w http.ResponseWriter, r *http.Request) {
		fmt.Fprintf(w, `
			<html>
			<head>
			<title>ABOUT PAGE</title>
			</head>
			<body>
			<h1>ABOUT ME:</h1>
			<p>I am 23 years old, live alone. and I really want to get a cat!!! lol<p>
			<h2>Here is a photo from my favorite game Falout 3 which shows pittsburgh in a post apolcalypse, kinda cool.</h2>
			<img src="https://static1.srcdn.com/wordpress/wp-content/uploads/2021/06/Fallout-3--The-Pitt-World-Story-Explained.jpg" height="500" width="500">
			</body>
			</html>
		`)
	})

	http.HandleFunc("/hobbys", func(w http.ResponseWriter, r *http.Request) {
		fmt.Fprintf(w, `
			<html>
			<head>
			<title>ABOUT PAGE</title>
			</head>
			<body>
			<h1>MY HOBBIES:</h1>
			<p>My favoirtie hobby that i have is playing the guitar and creating stupid songs with freinds.<p>
			<img src="data:image/jpeg;base64," height="500" width="500">
			</body>
			</html>
		`)
	})

	fmt.Println("Server listening on port source 8080...")
	http.ListenAndServe(":8080", nil)
}
